const feedbackLink=document.querySelector(".contacts-button"),contactPopup=document.querySelector(".modal"),contactClose=contactPopup.querySelector(".modal-close"),popupOverlay=document.querySelector(".overlay");feedbackLink.addEventListener("click",function(e){e.preventDefault(),contactPopup.classList.add("modal-show"),popupOverlay.classList.add("modal-show")}),contactClose.addEventListener("click",function(e){e.preventDefault(),contactPopup.classList.remove("modal-show"),popupOverlay.classList.remove("modal-show")});
